package com.xiaopengwei.springboot.admin.server;import de.codecentric.boot.admin.server.config.EnableAdminServer;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.EnableAutoConfiguration;import org.springframework.context.annotation.Configuration;/** * <p> * admin - Server 启动类 * </p> * * @author github.com/xpwi * @since 2019-04-17 */@Configuration@EnableAutoConfiguration@EnableAdminServerpublic class AdminServerApplication {    public static void main(String[] args) {        SpringApplication.run(AdminServerApplication.class, args);        System.out.println("admin 控制台在：http://127.0.0.1:8080/#/");    }}