package com.xiaopengwei.springboot.cache;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.EnableAutoConfiguration;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.cache.annotation.EnableCaching;import org.springframework.context.annotation.ComponentScan;/** * <p> * Cache - 启动类 * </p> * * @author github.com/xpwi * @since 2019-04-20 */@SpringBootApplication@EnableCachingpublic class CacheApplication {    public static void main(String[] args) {        SpringApplication.run(CacheApplication.class, args);    }}