package com.xiaopengwei.thread;/** * <p> * 定义线程中执行内容 * </p> * * @author github.com/xpwi * @since 2019-04-18 */public class MyThread extends Thread {    private int i;    @Override    public void run(){        for (;i<10;i++){            System.out.println("第"+i+"个 MyThread："+getName());        }    }}