package com.xiaopengwei.thread;/** * <p> * Runnable 接口的实现 * </p> * * @author github.com/xpwi * @since 2019-04-18 */public class MyThreadByRunable implements Runnable {    private int i;    @Override    public void run() {        for (;i<10;i++){            System.out.println("第"+i+"个 Thread："+Thread.currentThread().getName());            if (i==20){                System.out.println(Thread.currentThread().getName()+"执行完毕");            }        }    }}