package com.xiaopengwei.thread;import java.util.concurrent.ExecutionException;import java.util.concurrent.FutureTask;/** * <p> * 测试 * </p> * * @author github.com/xpwi * @since 2019-04-18 */public class ThreadByCallableTest {    public static void main(String[] args) {        MyThreadByCallable myThreadByRunable = new MyThreadByCallable();        FutureTask<Integer> futureTask = new FutureTask<>(myThreadByRunable);        Thread thread01 = new Thread(futureTask, "线程1");        thread01.start();        try {            System.out.println(futureTask.get());        } catch (InterruptedException | ExecutionException e) {            e.printStackTrace();        }    }}