package com.xiaopengwei.springboot.jpa;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.boot.autoconfigure.domain.EntityScan;/** * <p> * Jpa - 启动类 * </p> * * @author github.com/xpwi * @since 2019-04-18 */@SpringBootApplication@EntityScan(basePackages = "com.xiaopengwei.springboot.jpa.entity")public class JpaApplication {    public static void main(String[] args) {        SpringApplication.run(JpaApplication.class, args);        System.out.println("\n启动成功 | 测试地址");        System.out.println("提示：/database 含执行 sql, 使用前请先检查是否配置数据库\n");        System.out.println("查询所有 | http://localhost:8080/user/list ");        System.out.println("查询姓名 | http://localhost:8080/user/{id} ");        System.out.println("提示 | 如果没有数据可能是数据库中数据被清掉了 ");        System.out.println("首页 | https://github.com/xpwi ");    }}