package com.xiaopengwei.springboot.multi;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;/** * <p> * 接口多实现动态调用 * * @author github.com/xpwi * @since 2020-12-05 */@SpringBootApplicationpublic class MultiServiceImplApplication {    public static void main(String[] args) {        SpringApplication.run(MultiServiceImplApplication.class, args);        System.out.println("\n========启动成功！=========");        System.out.println("提示：StudentController 采用 @Autowire 注入 Map");        System.out.println("提示：TeacherController 采用 ApplicationContextAware");        System.out.println("Swagger UI 地址 | http://127.0.0.1:8080/swagger-ui.html");        System.out.println("@Author    | https://github.com/xpwi\n");    }}