package com.xiaopengwei.springboot.mybatis;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import springfox.documentation.swagger2.annotations.EnableSwagger2;/** * <p> * 因比较常用故已配置 MyBatis Generator + Swagger + logback 等 * 启动类 * </p> * * @author Xiao pengwei.com * @since 2019-04-07 */@EnableSwagger2@SpringBootApplicationpublic class MyBatisApplication {    public static void main(String[] args) {        SpringApplication.run(MyBatisApplication.class, args);        System.out.println("\n========启动成功！=========");        System.out.println("提示：/database 含执行 sql, 使用前请先检查是否配置数据库\n");        System.out.println("Swagger UI 地址 | http://127.0.0.1:8080/swagger-ui.html");        System.out.println("@Author    | https://github.com/xpwi\n");    }}