package com.xiaopengwei.springboot.mybatis.util;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import springfox.documentation.builders.ApiInfoBuilder;import springfox.documentation.builders.PathSelectors;import springfox.documentation.builders.RequestHandlerSelectors;import springfox.documentation.service.ApiInfo;import springfox.documentation.spi.DocumentationType;import springfox.documentation.spring.web.plugins.Docket;/** * <p> * Swagger API 配置类 * </p> * * @author Xiao pengwei * @since 2019-04-12 */@Configurationpublic class SwaggerConfig {    @Bean    public Docket createRestApi() {        return new Docket(DocumentationType.SWAGGER_2)                .apiInfo(apiInfo())                .select()                .apis(RequestHandlerSelectors.basePackage("com.xiaopengwei.springboot"))                .paths(PathSelectors.any())                .build();    }    private ApiInfo apiInfo() {        return new ApiInfoBuilder()                .title("SpringBoot使用Swagger构建api文档")                .description("简单优雅的 restfun 风格 | https://github.com/xpwi")                .termsOfServiceUrl("https://github.com/xpwi")                .version("1.0")                .build();    }}