package com.xiaopengwei.springboot.multi.sources.config;import org.springframework.boot.context.properties.ConfigurationProperties;import org.springframework.stereotype.Component;/** * <p> * 测试 * </p> * * @author github.com/xpwi * @since 2019-04-19 */@ConfigurationProperties(prefix = "myself.info")@Componentpublic class UserDevProperties {    private String name;    private String age;    private String email;    private String home;    private String greeting;    public String getGreeting() {        return greeting;    }    public void setGreeting(String greeting) {        this.greeting = greeting;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getAge() {        return age;    }    public void setAge(String age) {        this.age = age;    }    public String getEmail() {        return email;    }    public void setEmail(String email) {        this.email = email;    }    public String getHome() {        return home;    }    public void setHome(String home) {        this.home = home;    }}