package com.xiaopengwei.springboot.mybatis.controller;import org.springframework.beans.factory.annotation.Value;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;/** * <p> * 自定义配置文件读取测试 * 方式一：直接读取文件 * </p> * * @author github.com/xpwi * @since 2019-04-19 */@RestControllerpublic class UserConfigMethod1Controller {    @Value("${myself.info.name}")    private String name;    @Value("${myself.info.age}")    private String age;    @Value("${myself.info.home}")    private String home;    @Value("${myself.info.greeting}")    private String greeting;    @RequestMapping(value = "/userConfig1")    public String userConfig(){        return "userConfig1: "+"\n>>>>"+greeting + "\n>>>>" + home;    }}