package com.xiaopengwei.springboot.rabbitmq.rabbit.famout;import org.springframework.amqp.core.AmqpTemplate;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Component;/** * <p> * 生产者 * </p> * * @author github.com/xpwi * @since 2019-04-20 */@Componentpublic class FanoutSender {    @Autowired    private AmqpTemplate rabbitTemplate;    public void send() {        String context = "hi, fanout msg ";        System.out.println("Sender : " + context);        this.rabbitTemplate.convertAndSend("fanoutExchange","", context);    }}