package com.xiaopengwei.springboot.rabbitmq.rabbit.hello;import org.springframework.amqp.rabbit.annotation.RabbitHandler;import org.springframework.amqp.rabbit.annotation.RabbitListener;import org.springframework.stereotype.Component;/** * <p> * 消费者 * </p> * * @author github.com/xpwi * @since 2019-04-20 */@Component@RabbitListener(queues = "hello")public class HelloReceiver {    @RabbitHandler    public void process(String hello) {        System.out.println("ReceiverTest  : " + hello);    }}