package com.xiaopengwei.springboot.rest.template.component;import com.xiaopengwei.springboot.rest.template.RestTemplateApplication;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.boot.CommandLineRunner;import org.springframework.boot.web.client.RestTemplateBuilder;import org.springframework.context.annotation.Bean;import org.springframework.stereotype.Component;import org.springframework.web.client.RestTemplate;/** * <p> * 执行 * </p> * * @author github.com/xpwi * @since 2019-04-20 */@Componentpublic class AppRunner {    private static final Logger log = LoggerFactory.getLogger(RestTemplateApplication.class);    @Bean    public RestTemplate restTemplate(RestTemplateBuilder builder) {        return builder.build();    }    @Bean    public CommandLineRunner run(RestTemplate restTemplate) throws Exception {        return args -> {            String quote = restTemplate.getForObject(                    "http://gturnquist-quoters.cfapps.io/api/random", String.class);            log.info(quote);        };    }}