package com.xiaopengwei.springboot.schedule.task;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.scheduling.annotation.Scheduled;import org.springframework.stereotype.Component;import java.text.SimpleDateFormat;import java.util.Date;/** * <p> * 测试定时任务 * </p> * * @author github.com/xpwi * @since 2019-04-20 */@Componentpublic class ScheduleTaskTest {    private static final Logger log = LoggerFactory.getLogger(ScheduleTaskTest.class);    private static final SimpleDateFormat dateFormat = new SimpleDateFormat("HH:mm:ss");    /**     * * @Scheduled(fixedRate = 5000) ：上一次开始执行时间点之后5秒再执行     * * @Scheduled(fixedDelay = 5000) ：上一次执行完毕时间点之后5秒再执行     * * @Scheduled(initialDelay=1000, fixedRate=5000) ：第一次延迟1秒后执行，之后按fixedRate的规则每5秒执行一次     * * @Scheduled(cron="  /5   ") ：通过cron表达式定义规则     *     * @author github.com/xpwi     */    @Scheduled(fixedRate = 5000)    public void reportCurrentTime() {        log.info("The time is now {}", dateFormat.format(new Date()));    }}