package com.xiaopengwei.springboot.swagger;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import springfox.documentation.swagger2.annotations.EnableSwagger2;/** * <p> * Swagger 测试 - 启动类 * </p> * * @author github.com/xpwi * @since 2019-04-20 */@EnableSwagger2@SpringBootApplicationpublic class SwaggerApplication {    public static void main(String[] args) {        SpringApplication.run(SwaggerApplication.class, args);        System.out.println("\n========启动成功！=========");        System.out.println("Swagger UI 地址 | http://127.0.0.1:8088/swagger-ui.html");        System.out.println("@Author    | https://github.com/xpwi\n");    }}