package com.xiaopengwei.springboot.swagger.controller;import com.xiaopengwei.springboot.swagger.entityvo.ReturnMessage;import io.swagger.annotations.Api;import io.swagger.annotations.ApiOperation;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RestController;/** * <p> * 测试 前端控制器 * </p> * * @author github.com/xpwi * @since 2019-04-08 */@Api(value = "stu", tags = {"xiaopengwei"})@RestControllerpublic class TeacherEOController {    /**     * 根据 ID 获取用户信息     * 测试：/getStudentById.do?studentId=1     *     * @param studentId     * @return com.xiaopengwei.ssm.entityvo.ReturnMessage     * @author github.com/xpwi     */    @ApiOperation(value = "根据ID查询教师")    @GetMapping("/tgetStudentById.do")    public ReturnMessage getStudentById(int studentId) {        ReturnMessage returnMessage = new ReturnMessage();        returnMessage.setState("0");        returnMessage.setMessage("根据ID查询学生");        return returnMessage;    }}